package com.ciscu24.realmeme.views;

import android.content.Intent;
import android.os.Bundle;

import com.ciscu24.realmeme.R;
import com.ciscu24.realmeme.interfaces.ListInterface;
import com.ciscu24.realmeme.models.MemeEntity;
import com.ciscu24.realmeme.presenters.ListPresenter;
import com.ciscu24.realmeme.views.FormActivity;
import com.google.android.material.floatingactionbutton.FloatingActionButton;

import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.util.Log;
import android.view.View;

import android.view.Menu;
import android.view.MenuItem;
import android.widget.Toast;

import java.util.ArrayList;

public class ListActivity extends AppCompatActivity implements ListInterface.View {

    String TAG = "Demo2021/ListActivity";
    private ListInterface.Presenter presenter;
    private ArrayList<MemeEntity> items;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_list);
        Toolbar toolbar = findViewById(R.id.toolbar);
        setSupportActionBar(toolbar);

        presenter = new ListPresenter(this);

        FloatingActionButton fab = findViewById(R.id.fab);
        fab.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Log.d(TAG, "Click on Floating Button");
                presenter.onClickAddMeme();
            }
        });

        //Creo una lista con los elementos a mostrar
        //ESTO ES PROVISIONAL HASTA QUE HAGAMOS EL MODELO EN LA UNIDAD 5
        items = new ArrayList<MemeEntity>();

        MemeEntity meme1 = new MemeEntity();
        meme1.setId("1");
        meme1.setImage("");
        //meme1.setImage("");
        meme1.setName("Holaaaaa");
        items.add(meme1);

        MemeEntity meme2 = new MemeEntity();
        meme2.setName("QueHaceLetsGo");
        meme2.setImage("");
        meme2.setId("2");
        items.add(meme2);

        // Inicializa el RecyclerView
        final RecyclerView recyclerView = (RecyclerView) findViewById(R.id.memelist);

        // Crea el Adaptador con los datos de la lista anterior
        MemeAdapter adapter = new MemeAdapter(items);

        // Asocia el elemento de la lista con una acción al ser pulsado
        adapter.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                // Acción al pulsar el elemento
                int position = recyclerView.getChildAdapterPosition(v);
                /*Toast.makeText(ListActivity.this, "Posición: " + String.valueOf(position) +" Name: " + items.get(position).getName(), Toast.LENGTH_SHORT)
                        .show();
                 */
                presenter.onClickRecyclerViewItem(items.get(position).getId());
            }
        });

        // Asocia el Adaptador al RecyclerView
        recyclerView.setAdapter(adapter);

        // Muestra el RecyclerView en vertical
        recyclerView.setLayoutManager(new LinearLayoutManager(this));
    }

    //Funcion para añadir el menu que queramos
    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        // Inflate the menu; this adds items to the action bar if it is present.
        getMenuInflater().inflate(R.menu.menu_list, menu);
        return true;
    }

    //Funcion para asignar funciones a los botones del menu
    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        // Handle action bar item clicks here. The action bar will
        // automatically handle clicks on the Home/Up button, so long
        // as you specify a parent activity in AndroidManifest.xml.
        int id = item.getItemId();

        if(id == R.id.action_search_list){
            presenter.onClickSearchToolbar();
        }

        if(id == R.id.action_about_list){
            presenter.onClickAboutToolbar();
        }

        return super.onOptionsItemSelected(item);
    }

    @Override
    public void startFormActivity() {
        Intent intent = new Intent(getApplicationContext(), FormActivity.class);
        startActivity(intent);
    }

    @Override
    public void startFormActivity(String id) {
        Intent intent = new Intent(getApplicationContext(), FormActivity.class);
        intent.putExtra("id", id);
        startActivity(intent);
    }

    @Override
    public void startAboutActivity() {
        Intent intent = new Intent(getApplicationContext(), AboutActivity.class);
        startActivity(intent);
    }

    @Override
    public void startSearchActivity() {
        Intent intent = new Intent(getApplicationContext(), SearchActivity.class);
        startActivity(intent);
    }

}